import React, { useEffect, useState } from 'react'
import Button from '@atlaskit/button';
import Card from './Card';
import AddNew from './AddNew';
import { v4 } from 'uuid';
export default function Body(props) {
  const [render,setRender] = useState(true)
  const onHandleChangeStatus = (e,index) => {
    const temp = e;
  switch (temp.status2) {
    case  "Doing":
      temp.status2 = 'Done'
      break;
    case  "Done":
      temp.status2 = 'New';
      break;
    case  "New":
      temp.status2 = 'Doing'
      break;

    default:
      break;
  }
    data.splice(index,1,temp);
    localStorage.setItem('data',JSON.stringify(data))
    setRender(!render)
  }
  const onHandleChangeNewTask = () => {
    console.log(1);
  }
  const [data,setData] = useState([]);
  useEffect(() => {
    setData(JSON.parse(localStorage.getItem('data')));
    console.log('dsadasd: ', JSON.parse(localStorage.getItem('data')));
  }, [localStorage.getItem('data')]);
  return (

    <div className='d-flex'>

    <div style={{width : "200px"}}>
    <Button appearance="warning" shouldFitContainer>All Task</Button>
    <Button onClick={onHandleChangeNewTask} appearance="warning" shouldFitContainer>New Task</Button>
    <Button appearance="warning" shouldFitContainer>Doing Task</Button>
    <Button appearance="warning" shouldFitContainer>Dont Task</Button>
    </div>
    <AddNew  display2={(props.status) ? "none" : "block"}/>
    <div className='flex-wrap p-3 gap-3' style={{display : (props.status) ? "flex" : "none"}}>
    {
      data.map((e,index) => <Card key={v4()} click={() => onHandleChangeStatus(e,index)} status2={e.status2} title={e.title} Creator={e.Creator} Description={e.Description}/>
      )
    }
    </div>
    </div>
  )
}
